import React from "react";
import { Link } from "react-router-dom";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";
const CountriesGql = gql`
  {
    countries {
      name
      emoji
      currency
      native
      languages {
        name
        native
        code
        rtl
      }
      continent {
        name
        code
      }
    }
  }
`;
const Country = props => {
  const { loading, error, data } = useQuery(CountriesGql);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  return (
    <>
      <h1>Hello From Country</h1>
      <ul>
        {data.countries.map(country => {
          return <li key={country.name + country.currency}>
            {<Link>{country.name} {country.code}</Link>}
          </li>;
        })}
        {/* <li>
          <Link to={`${props.match.path}/hello1`}>Hello1</Link>
        </li>
        <li>
          <Link to={`${props.match.path}/hello2`}>Hello2</Link>
        </li>
        <li>
          <Link to={`${props.match.path}/hello3`}>Hello3</Link>
        </li>
        <li>
          <Link to={`${props.match.path}/hello4`}>Hello4</Link>
        </li> */}
      </ul>
    </>
  );
};

export default Country;
