import React, { useState } from "react";
import { Link } from "react-router-dom";
import Button from "../Button";
import "./listCard.css";
import styled from "styled-components";
const showLanguages = languages => {
  return (
    <>
      <h1>Languages</h1>
      {languages.map(language => {
        return (
          <div className="card-extra">
            <h3>Native: {language.native}</h3>
            <h3>English: {language.name}</h3>
          </div>
        );
      })}
    </>
  );
};

const ListCard = ({ country, match }) => {
  const [isExpand, setIsExpand] = useState(false);
  return (
    <div className="card">
      <div className="card-body">
        <div className="card-text-box">
          <h1>Country Name: {country.name}</h1>
          <h1>Continent: {country.continent.name}</h1>
        </div>
        <div className="card-btn-box">
          <Link to={`${match.path}/${country.code}`}>
            <Button>Show Detatil</Button>
          </Link>
          <Button handleClick={() => setIsExpand(isExpand => !isExpand)}>
            {isExpand ? "Hide Languages" : "Show Languages"}
          </Button>
        </div>
      </div>

      {isExpand ? showLanguages(country.languages) : null}
    </div>
  );
};
export default ListCard;

const Card = styled.div`
  max-width: 400px;
  min-height: 200px;
  background-color: #ffff;
  border: 1px solid rgba(255, 255, 255, 0.233);
  box-shadow: 0px 0px 75px -18px rgba(0, 0, 0, 0.75);
  margin: 20px;
  border-radius: 20px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: flex-start;
  transition: all 0.1s ease-in;
  padding: 10px;

  :hover {
    transform: scale(1.1, 1.1);
  }
`;

const CardBody = styled.div`
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  width: 100%;
  padding: 5px;
`;

const CardTextBox = styled.div`
  flex: 2;
`;
const CardButtonBox = styled.div`
  flex: 1;
  align-items: center;
  height: 100%;
  display: flex;
  flex-direction: column;
`;
const CardExtra = styled.div`
 display: flex;
  width: 100%;
  justify-content: space-evenly;
`;
