import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./card.css";

const showLanguages = languages => {
  return languages.map(language => {
    return (
      <div className="card-extra">
        <h3>Native {language.native}</h3>
        <h3>English {language.name}</h3>
      </div>
    );
  });
};

const ListCard = ({ country, match }) => {
  const [isExpand, setIsExpand] = useState(false);
  return (
    <div className="card">
      <div className="card-body">
        <div className="card-text-box">
          <h1>Country Name {country.name}</h1>
          <h1>Continent {country.continent.name}</h1>
        </div>
        <div className="card-btn-box">
        <Link>
          <button  className="expand-card-btn">
            Show Deatil
          </button>
          </Link>
          <button
            onClick={() => setIsExpand(isExpand => !isExpand)}
            className="expand-card-btn"
          >
            {isExpand ? "Hide Languages" : "Show Languages"}
          </button>
        </div>
      </div>

      {isExpand ? showLanguages(country.languages) : null}
    </div>
  );
};
export default ListCard;
