import React from "react";
import { Link } from "react-router-dom";
import { useQuery } from "@apollo/react-hooks";
import { gql } from "apollo-boost";

import { ListCard } from "../../components/Country";

const COUNTRIES_QUERY = gql`
  {
    countries {
      name
      code
      continent {
        name
      }
      languages {
        name
        native
      }
    }
  }
`;
const Countries = ({ match }) => {
  const { loading, error, data } = useQuery(COUNTRIES_QUERY);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  return (
    <>
      <h1>Hello From Country</h1>
      <ul>
        {data.countries.map(country => {
          console.log(country);
          return (
            <li key={country.name + country.currency}>
              {
                <>
                  <Link to={`${match.path}/${country.code}`}>
                    {country.name} {country.code}
                  </Link>
                  <h3>Continent::=> {country.continent.name}</h3>
                  {country.languages.map(language => {
                    return (
                      <>
                        <h3>Native::=> {language.native}</h3>
                        <h3>English::=> {language.name}</h3>
                      </>
                    );
                  })}
                </>
              }
            </li>
          );
        })}
      </ul>
    </>
  );
};

export default Countries;
